package com.abdurashidov.valyuta.adaptersimport androidx.fragment.app.Fragmentimport androidx.viewpager2.adapter.FragmentStateAdapterimport com.abdurashidov.valyuta.models.PagerItemimport com.abdurashidov.valyuta.ui.ValyutaConverterFragmentimport com.abdurashidov.valyuta.ui.ValyutaListFragmentclass StateAdapters(val list:ArrayList<PagerItem>, fragment: Fragment)    : FragmentStateAdapter(fragment){    override fun getItemCount(): Int {        return list.size    }    override fun createFragment(position: Int): Fragment {        return when(position){            0-> ValyutaListFragment()            1-> ValyutaConverterFragment()            else-> ValyutaListFragment()        }    }}